import { ADD_FAV, REMOVE_FAV, FILTER, ORDER } from "./actions/actions_types";

const initialState = {
    myFavorites: [],
    allCharacters: []
}

function rootReducer(state = initialState, {type, payload}) {
    switch(type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites:[...state.allCharacters, payload],
