import styled from "styled-components";

export const DivSearch = styled.div`
    padding: 1em;
    display: flex;
    justify-content: center;
`

export const Input = styled.input`
    border-radius: 0.5em;
    background-color: gray;
`

export const Button = styled.button`
    border-radius: .5em;
    
`
